  name: 'authorName',
  type: 'string',
  default: '',
  description: 'Reviewer name to show inside the review card',
}, {
  name: 'authorPhoto',
  type: 'object',
  default: '',
  description: 'Reviewer photo to show inside the review card.Should be an object with src and srcSet properties',
}, {
  name: 'icon',
