import React from 'react';
import PropTypes from 'prop-types';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import { useMediaQuery, Grid, Typography } from '@material-ui/core';
import { Image , Icon } from 'components/atoms';
import { SectionHeader } from 'components/molecules';
import { colors } from '@material-ui/core';


const useStyles = makeStyles(theme => ({
  image: {
    maxWidth: 400,
  },
}));

const AboutTop = props => {
  const { className, ...rest } = props;
  const classes = useStyles();

  const theme = useTheme();
  const isMd = useMediaQuery(theme.breakpoints.up('md'), {
    defaultMatches: true,
  });

  return (
    <div className={className} {...rest}>
      <Grid container justify="space-between" spacing={isMd ? 4 : 2}>
        <Grid
          item
          container
          justify="center"
          alignItems="flex-start"
          xs={12}
          md={6}
          data-aos={'fade-up'}
        >
          <Image
            src="https://ik.imagekit.io/j6phbjneuop/premasweb/premas_ceo_2_HRTFrMnum.png"
            alt="Find a Job"
            className={classes.image}
          />
        </Grid>
        <Grid
          item
          container
          alignItems="center"
          xs={12}
          md={6}
          data-aos={'fade-up'}
        >
          <div>

            <SectionHeader
              title="Founder Chairman & CEO"

              subtitle="Send one-off and automated email, push, and in-app messages to people. Create better stories."
              align="left"
            />
            <Typography align="left" variant="h6" color="textSecondary">
            Avijit was the Group Director, Eastern Hemisphere Operations for Gillette-Duracell and a management consultant with AT Kearney before becoming an entrepreneur. He is associated with several enterprises
            across the globe and has significant management experience of more than 30 years now
            </Typography>

            <br/>
            <div style={{ flexDirection: 'row', justifyContent: 'space-between' }}>
            <Icon fontIconClass="fab fa-facebook-square" fontIconColor={colors.pink[50]} />
            <Icon fontIconClass="fab fa-twitter-square" fontIconColor={colors.pink[50]} />
            <Icon fontIconClass="fab fa-linkedin" fontIconColor={colors.pink[50]} />

            </div>
          </div>
        </Grid>
      </Grid>
    </div>
  );
};

AboutTop.propTypes = {
  /**
   * External classes
   */
  className: PropTypes.string,
};

export default AboutTop;
